    GameUi.prototype.update = function () {
        // make things rotate
        this.rotateTo();
        // rotate the hue of the graduated level progress bar to create a fluctuating effect
        //this._colorMatrix.hue(Math.round(Game._now / 10));
        var ds = this._scoreTarget - this._scoreShowing;
        if (Math.abs(ds) >= 1) {
            this._scoreShowing = Math.round(this._scoreShowing + Math.max(ds / 10, 1));
            this._scoreText.text = this.formatBigNumber(this._scoreShowing);
        }
        // level progress is measured as a percentage (0..1), convert it into pixels
        var dp = (this._levelProgressTarget - this._levelProgressShowing);
        var lp = dp * this.LevelProgressWide;
        if (Math.abs(lp) >= 1) {
            if (lp > 0) {
                this._levelProgressShowing = this._levelProgressShowing + dp / 10;
            }
            else {
                this._levelProgressShowing = 0;
            }
            this._levelProgressMask.x = -this.LevelProgressWide + Math.round(this._levelProgressShowing * this.LevelProgressWide);
        }
    };
