    GameUi.prototype.removeBoostHighlight = function (_resize) {
        if (_resize === void 0) { _resize = true; }
        // disable highlights
        this._whichButtonHighlight = -1;
        if (_resize)
            this.resize(this.scale.x);
    };
    GameUi.prototype.highlightBoostButton = function (whichButton) {
        this.removeBoostHighlight();
        this._whichButtonHighlight = whichButton;
        // show the selected button as a highlight
        var cb = null;
        switch (whichButton) {
            case 0:
                cb = this._boostPaintContainer;
                break;
            case 1:
                cb = this._boostDestroyContainer;
                break;
            case 2:
                cb = this._boostGrowContainer;
                break;
        }
        var p = FUE_1.FUE.getGlobalPosition(cb, this._blanker);
        var sf = FUE_1.FUE.getGlobalScale(cb.parent);
        var st = FUE_1.FUE.getGlobalScale(this._blanker);
        this.reparent(cb, this._blanker);
        cb.x = p.x / st.x;
        cb.y = p.y / st.y;
        cb.scale.x = cb.scale.x / st.x * sf.x;
        cb.scale.y = cb.scale.y / st.y * sf.y;
    };
    GameUi.prototype.boostClickedBoard = function (boardPos) {
        switch (this._boostMode) {
            case Enums_1.BoostModes.BoostPaint:
                this.choosePaint(boardPos);
                break;
            case Enums_1.BoostModes.BoostDestroy:
                this._emitter.emit("destroy_piece", boardPos);
                break;
            case Enums_1.BoostModes.BoostGrow:
                this._emitter.emit("grow_piece", boardPos);
                break;
        }
    };
    GameUi.prototype.choosePaint = function (boardPos) {
        this._paintPiece = PlayArea_1.PlayArea.GetPiece(boardPos);
        if (this._paintPiece != null) {
            var ci = this._paintPiece._colourIndex;
            // create mini-panel over Piece with two paint colour buttons
            var otherColours = [[1, 2], [0, 2], [0, 1]];
            this.createPaintChooser(otherColours[ci][0], otherColours[ci][1], ci);
            Audio_1.Audio.playSfx(Audio_1.Sfx.button_press);
            // disable board selection, but leave dragging disabled until colour is picked
            this._emitter.emit("boost_select", null);
        }
    };
